public class FizzBuzz {

	public static final int MAX = 30;

	public static void main(String[] args) {

		StringBuilder sb = new StringBuilder();

		// 速度重視(意味あるか不明)
		for (int i = 1; i <= MAX; i++) {
			if(i%3==0 && i%5==0) sb.append("Fizz Buzz ");
			else if(i%3==0) sb.append("Fizz ");
			else if(i%5==0) sb.append("Buzz ");
			else sb.append(i).append(" ");
		}

		sb.deleteCharAt(sb.length()-1);	// 最後の空白を削除
		System.out.println(sb.toString());

	}
}

import java.util.ArrayList;
import java.util.List;

public class Prime {

	public static final int MAX = 100;

	public static void main(String[] args) {

		List<Integer> primes = new ArrayList<>();
		boolean[] search = new boolean[MAX+1];

		// 探索リスト初期化
		for (int i = 3; i <= MAX; i += 2) search[i] = true;

		primes.add(2); // 2はあらかじめ入れておく
		for (int i = 3; i <= MAX; i+=2) {
			if (!search[i]) continue;
			primes.add(i);
			for (int j = i; j <= MAX; j+=i) search[j] = false;
		}

		for(Integer prime:primes) System.out.print(prime+" ");

	}
}
