item.body:
//昇順ソートを行うクラス
public class Bubble {
	public static void main(String[] args) {
		// データの格納
		int[] data = { 4, 7, 10, 2, 5 };
		// 昇順ソート
		for (int i = 0; i < data.length - 1; i++) {
			for (int j = 0; j < data.length - i - 1; j++) {
				if (data[j] > data[j + 1]) {
					int asc = data[j];
					data[j] = data[j + 1];
					data[j + 1] = asc;
				}
			}
		}
		// ソート後の結果を出力
		for (int i = 0; i < data.length; i++) {
			System.out.print(data[i] + " ");
		}
	}
}
